customhtml2.customJS:
require(['jquery'], function($){
	$(function(){
		function outsideClickListener(event) {
		    $target = $(event.target);
		    if (!$target.closest($("#interstitial")).length && $("#interstitial").is(':visible')) {
		        $(".klarna-upstream-interstitial__overlay__content__close button").trigger("click");
		        removeClickListener();
		    }
		}
		function removeClickListener() {
		    document.removeEventListener('click', outsideClickListener)
		}
		function hideOnClickOutside(selector) {
		  document.addEventListener('click', outsideClickListener)
		}
	   
	    hideOnClickOutside($("#interstitial"));

		$("#klarna-price-interstitial").on("closed.zf.reveal", function(){		    
		    removeClickListener();
		});
	});

});
