customhtml2_copy_cop._customCSS:
div.discount-container {
    margin: 50px auto;
    padding: 0 10%;
}

@media only screen and (min-width: 1024px) {
    div.discount-container {
        padding: 0 5%;
        max-width: 1024px;
    }
}

span.discount-bold {
    font-weight: 900;
}

div.table-container {
    border: #000 solid 2px;
}

table.discount-table {
    border-collapse: collapse;
    border-spacing: 0;
    width: 100%;
}

tbody tr:nth-child(even) {
    background-color: #fff;
}

tr.line-separator {
    border-bottom: #000 solid 2px!important;
}

th.discount-th {
    background-color: #000;
    color: #fff;
    font-weight: 900;
    border: #000 solid 2px;
}
