require(['jquery', 'domReady'], function ($, domReady) {
  domReady(function () {
    $(document).ready(function () {
      sizeRow();
    });
    $(window).resize(sizeRow);

    function sizeRow() {
      if ($(window).width() > 641) {
        var $col = $('.sizeRow .size-by-me');
        var height = $col.height();
        $col.siblings('.size-me').height(height);
      }
    }
  });
});
