      familyLinkMember.removeAttribute('tabindex');
      familyLinkMember.removeAttribute('data-push');
      var vfTokenOne_1 = brandUtil.getCookieWildCard('vf_loyalty_access_token_10153');
      console.log('VF access token: ', vfTokenOne_1.value);
      $.ajax({
        url: 'https://family.vans.com/api/profile',
        type: 'GET',
        headers: {
          Accept: 'application/vnd.stellar-v1+json'
        },
        data: {
          access_token: vfTokenOne_1.value
        },
        success: function success(response) {
          console.log('VF token is still valid');
          console.log('api/profile success response: ', response);
          $.ajax({
            url: 'https://family.vans.com/api/activities',
            type: 'GET',
            headers: {
              Accept: 'application/vnd.stellar-v1+json'
            },
            data: {
              with_metric_change: false,
              access_token: vfTokenOne_1.value
            },
            success: function success(res) {
              console.log('api/activities success response: ', res);
            },
            error: function error(err) {
              console.log('api/activities failed response: ', err);
            }
          });
        },
        error: function error(_error) {
          console.log('Cheetah profile auth failed. VF token is expired.');
          console.log('api/profile failed response: ', _error);
          var vfRefreshToken = brandUtil.getCookieWildCard('vf_loyalty_refresh_token_10153');
          console.log('vf refresh token: ', vfRefreshToken);
          $.ajax({
            url: 'https://feed.vans.com/api/cheetah/refresh',
            type: 'POST',
            contentType: 'application/x-www-form-urlencoded',
            data: {
              refresh_token: vfRefreshToken.value
            },
            success: function success(res) {
              console.log('Refresh token sent!');
              console.log('api/cheetah success response: ', res);
              console.log('New token: ', res.access_token);
              document.cookie = 'vf_loyalty_access_token_10153=; expires=Thu, 01 Jan 1970 00:00:00 UTC; path=/;';
              document.cookie = 'vf_loyalty_access_token_10153=' + res.access_token;
              brandUtil.setCookie('vf_loyalty_refresh_token_10153', res.refresh_token, 3);
            },
            error: function error(err) {
              console.log('Refresh token failed!');
              console.log('api/cheetah failed response: ', err);
              alert("Looks like something's going on. Try logging out and loggin back in.");
            }
          });
        }
      });
    } else {
      console.log('not a member or not signed in');
    }
  };

  brandUtil.sendAnalytics = function (clickLocation, clickValue) {
    var brandCampaign = 'altra-campaign';

    try {
      if (dataLayer) {
        dataLayer.push({
          event: 'brandCampaignClick',
          brandCampaignName: brandCampaign,
          clickLocation: clickLocation,
          clickValue: clickValue
        });
      }
    } catch (e) {
      console.log('dataLayer not found for ' + clickLocation + ' - ' + clickValue);
    }
  };

  brandUtil.getInfo = function (data, callback) {
    if (callback === void 0) {
      callback = null;
    }

    $.ajax({
      url: 'https://njot1.vans.com/gift-guide-19-latest/info/index.php',
      type: 'GET',
      dataType: 'html',
      crossDomain: true,
      data: data,
      success: function success(html) {
        if (callback) {
          callback(JSON.parse(html));
        }
      }
    });
  };

  brandUtil.postInfo = function (data, callback) {
