      }
    });
  };

  brandUtil.showLoader = function (loader) {
    if (loader === void 0) {
      loader = null;
    }

    var loaderBlock = loader ? loader : $('.loading-spinner');
    gsap.to(loaderBlock, {
      duration: 0.5,
      opacity: 1
    });
    loaderBlock.css('visibility', 'visible');
  };

  brandUtil.hideLoader = function (loader) {
    if (loader === void 0) {
      loader = null;
    }

    var loaderBlock = loader ? loader : $('.loading-spinner');
    gsap.to(loaderBlock, {
      duration: 0.5,
      opacity: 0
    });
    loaderBlock.css('visibility', 'hidden');
  };

  brandUtil.stickyScroll = function (stickyElem, container) {
    var elem = document.getElementById(stickyElem);
    var elemHt = elem.offsetHeight;
    var elemContainer = document.querySelector(container);
    var stuck = false;
    var stickPoint = elem.offsetTop;

    window.onscroll = function (e) {
      var distance = elem.offsetTop - window.pageYOffset;
      var offset = window.pageYOffset;

      if (distance <= -155 && !stuck) {
        elem.classList.add('sticky__active');
        elemContainer.style.paddingTop = elemHt + 'px';
        stuck = true;
      } else if (stuck && offset <= stickPoint) {
        elem.classList.remove('sticky__active');
        elemContainer.style.paddingTop = '0px';
        stuck = false;
      }
    };
  };

  brandUtil.singleScroll = function (scrollTarget, speed, animateDelay, scrollElement) {
    if (scrollElement === void 0) {
      scrollElement = $('html, body');
    }

    setTimeout(function () {
      $(scrollElement).animate({
        scrollTop: $(scrollTarget).offset().top
      }, speed);
    }, animateDelay);
  };

  brandUtil.mobileSubnav = function (navToggleBtn, navItem, navWrap) {
    var mqTab = window.matchMedia('(max-width: 640px)');

    if (mqTab.matches) {
      navToggleBtn.on('click', function () {
        if (!navWrap.hasClass('closed')) {
          gsap.to(navWrap, 0.25, {
            height: 0
          });
          navWrap.addClass('closed');
          navToggleBtn.removeClass('active');
        } else {
          gsap.set(navWrap, {
            height: 'auto'
          });
          gsap.from(navWrap, 0.25, {
            height: 0,
            ease: 'back.out.config(1.4)'
          });
          navToggleBtn.addClass('active');
          navWrap.removeClass('closed');
        }
      });
      navItem.on('click', function () {
        brandUtil.singleScroll($('#topOfPg'), 500);
        gsap.to(navWrap, 0.25, {
          height: 0
        });
        navWrap.addClass('closed');
        navToggleBtn.removeClass('active');
      });
    }
  };

  brandUtil.sSlider = function (slider, dot, arrow, center, autoPlay, slidesShown, screenSize, infiniteLoop) {
    if (infiniteLoop === void 0) {
      infiniteLoop = false;
    }

    var slides;
    var centered;
    var mqMobile = window.matchMedia('(min-width: 640px)');

    if (mqMobile.matches) {
      slides = 3;
      centered = false;
    } else {
      slides = 1;
      centered = true;
    }

    $(slider).each(function (i, el) {
      var $carousel = $(el);

      if ($(window).width() > screenSize) {
        if ($carousel.hasClass('slick-initialized')) {
          $carousel.slick('unslick');
        }
      } else {
        if (!$carousel.hasClass('slick-initialized')) {
          $carousel.slick({
            dots: dot,
            arrows: arrow,
            centerMode: center,
            centerPadding: '20px',
            slidesToShow: slides,
            infinite: infiniteLoop,
            autoplay: autoPlay,
            prevArrow: "<button type='button' class='slick-prev'><img src='https://www.vans.com/ns/vans/images/icon-slider-prev-black.png' /></button>",
            nextArrow: "<button type='button' class='slick-next'><img src='https://www.vans.com/ns/vans/images/icon-slider-prev-black.png' /></button></button>"
          });
        }
      }
    });
  };

  brandUtil.ctaDot = function () {
    function isTouchEnabled() {
      return 'ontouchstart' in window || navigator.maxTouchPoints > 0 || navigator.msMaxTouchPoints > 0;
    }

    var ctaWrap = $('.wayvee-shop__slider-item');
    ctaWrap.each(function (i, el) {
      var ctaTrigger = $(el).find('.dot__trigger');
      var ctaElem = $(el).find('.dot__cta');
      var tlCta = new TimelineMax({
        paused: true
      }).staggerTo(ctaElem, 0.35, {
        y: 0,
        opacity: 1
      }, 0.15);

      if (isTouchEnabled()) {
        $(el).on('touchstart', function () {
          tlCta.play();
        });
        document.addEventListener('touchstart', function (event) {
          var ev = event.target;

          if (!ev.closest('.wayvee-shop__item-prod-dot')) {
            tlCta.reverse();
          }
        }, false);
      } else {
        $(el).on('mouseenter', function () {
          tlCta.play();
        });
        $(el).on('mouseleave', function () {
          tlCta.reverse();
        });
      }
    });
  };

  brandUtil.lightBoxToggle = function () {
    $(document).on('click', '.lightbox-toggle', function (e) {
      brandUtil.lightBox(e);
    });
    $('.lightbox-close').on('click', brandUtil.lightBoxClose);
    $('.lightbox-bg').on('click', brandUtil.lightBoxClose);
  };

  brandUtil.lightBoxClose = function (e) {
