    $('.lightbox-content__wrap').remove();
  };

  brandUtil.lightBox = function (e) {
    var container = $('.lightbox-container');
    e.preventDefault();
    $('.video-lightbox').fadeToggle('fast');
    var context = $(e.currentTarget).attr('data-lightbox-type');
    var content = $(e.currentTarget).attr('data-lightbox-content');
    var ilContent = $(e.currentTarget).attr('data-interest');

    if (context === 'video') {
      container.append("\\n\\t\\t        <div class=\\"lightbox-content__wrap lightbox-video\\">\\n\\t\\t        <iframe class=\\"vid-frame\\" src=\\"" + content + "\\" frameborder=\\"0\\" allow=\\"accelerometer; autoplay; encrypted-media; gyroscope; picture-in-picture\\" allowfullscreen> </iframe>\\n\\t\\t        </div>\\n\\t\\t    ");
    }

    if (context === 'interestlist') {
      container.append("\\n\\t\\t\\t\\t<div class=\\"lightbox-content__wrap lightbox-video\\">\\n\\t\\t\\t\\t<div class=\\"lightbox-content__inner modal__inner\\">\\n\\t\\t\\t\\t<img class=\\"modal__header-img\\" src=\\"https://images.vans.com/is/image/VansBrand/wayvee-vert-checker?$scale-original$\\" alt=\\"Vans Skate\\"/>\\n\\t\\t\\t\\t<h1 class=\\"modal__lockup\\"><span>The&nbsp;</span>Wayvee</h1>\\n\\t\\t\\t\\t<h2 class=\\"modal__title\\">" + WayveeInterestLists.interestLists[ilContent].title + "</h2>\\n\\t\\t\\t\\t<h3 class=\\"modal__subtitle\\">Available: " + WayveeInterestLists.interestLists[ilContent].dropDate + "</h3>\\n\\t\\t\\t\\t<img class=\\"modal__footer-img\\" src=\\"https://images.vans.com/is/image/VansBrand/logo-vans-skate?$scale-original$&fmt=png8-alpha\\" alt=\\"Vans Skateboarding\\">\\n\\t\\t\\t\\t</div>\\n\\t\\t\\t\\t</div>\\n\\t\\t\\t");
    }
  };

  brandUtil.getRandomColor = function () {
    var letters = '0123456789ABCDEF';
    var color = '#';

    for (var i = 0; i < 6; i++) {
      color += letters[Math.floor(Math.random() * 16)];
    }

    return color;
  };

  brandUtil.shuffleArray = function (a) {
    var _a;

    for (var i = a.length - 1; i > 0; i--) {
      var j = Math.floor(Math.random() * (i + 1));
      _a = [a[j], a[i]], a[i] = _a[0], a[j] = _a[1];
    }

    return a;
  };

  return brandUtil;
}();

var BrandImagePreloader = function (_super) {
  __extends(BrandImagePreloader, _super);

  function BrandImagePreloader(preloadClass) {
    if (preloadClass === void 0) {
      preloadClass = '.vimg-preload';
    }

    var _this = _super.call(this) || this;

    _this.preload = function () {
      $(_this.preloadClass).each(function (i, e) {
        _this.imgTotal++;
        var img = $(e);
        var src = img.attr('src');
        img.attr('src', '');
        img.on('load', _this.loaded);
        img.attr('src', src);
      });
    };

    _this.loaded = function (e) {
      _this.imgLoaded++;

      _this.trigger(BrandImagePreloader.IMG_LOADED, $(e.currentTarget));

      if (_this.imgLoaded === _this.imgTotal) {
        _this.trigger(BrandImagePreloader.COMPLETE);
      }
    };

    _this.preloadClass = preloadClass;
    _this.imgTotal = 0;
    _this.imgLoaded = 0;
    return _this;
  }

  BrandImagePreloader.COMPLETE = 'complete';
  BrandImagePreloader.IMG_LOADED = 'img_loaded';
  return BrandImagePreloader;
}(AltraEventDispatcher);

