var ArcsineViewerApp = function () {
  function ArcsineViewerApp(el) {
    this.el = $(el);
    ArcsineViewerApp.loader = this.el.find('.loading-spinner');
  }

  ArcsineViewerApp.loadViewer = function (projectId, isFirstLoad) {
    var viewer;
    var sdkScript = document.createElement('script');
    sdkScript.src = 'https://www.altrarunning.com/ns/altra/scripts/sdk.bundle.js';
    sdkScript.defer = true;
    var mqTab = window.matchMedia('(min-width: 641px)');
    var thumbnailNav;

    if (mqTab.matches) {
      thumbnailNav = 'thumbnails';
    } else {
      thumbnailNav = 'none';
    }

    var slideShowSlides;

    if (isFirstLoad && mqTab.matches) {
      slideShowSlides = [0, 5, 6, 8, 10, 0];
    } else {
      slideShowSlides = '';
    }

    sdkScript.onload = function () {
      viewer = window.arcsineImaging.createViewer(document.getElementById('viewerTest'), {
        id: projectId,
        assetHostUrl: 'https://www.altrarunning.com/ns/altra',
        initialViewpoint: 0,
        autoLoad: false,
        navigation: thumbnailNav,
        autoSlideshowOrder: slideShowSlides,
        autoSlideshowDurations: 850,
        autoSlideshowDelay: 500
      });
      viewer.load().then(function () {
        gsap.to('.wayvee-viewer', {
          delay: 1,
          duration: 0.5,
          opacity: 1
        });
        gsap.to('.content-wrap', {
          delay: 1,
          duration: 0.5,
          opacity: 1
        });
        brandUtil.hideLoader(ArcsineViewerApp.loader);

        if (isFirstLoad) {
          brandUtil.singleScroll($('#wayveeHeader'), 500, 1250);
        }

        if (!mqTab.matches) {
          gsap.to('.mobile-instructions', {
            duration: 0.5,
            autoAlpha: 1,
            delay: 2
          });
          var wv = document.querySelector('.wayvee-viewer');

          var hideViewerInstructions = function hideViewerInstructions() {
            gsap.to('.mobile-instructions', {
              duration: 1,
              autoAlpha: 0
            });
          };

          wv.addEventListener('touchstart', hideViewerInstructions, false);
        }
        var viewerThumbnail = document.querySelectorAll('.__arc-noselect');
        viewerThumbnail.forEach(function (thumb) {
          thumb.addEventListener('click', function () {
            gsap.to('#techFeature', {
              duration: 0.25,
              opacity: 0
            });
          });
        });
      });
    };

    document.head.appendChild(sdkScript);
  };

  return ArcsineViewerApp;
}();

var ModuleFactory = function () {
  function ModuleFactory() {}

  ModuleFactory.prototype.createModule = function (name) {
    switch (name) {

      case 'modVidImg':
        return new VidImageModule();

      case 'modVid':
        return new VideoModule();

      case 'modShop':
        return new ShopModule();

      case 'modBioImg':
        return new BioImagesModule();

      case 'modBioTxt':
        return new BioTextModule();

      default:
        return null;
    }
  };

  return ModuleFactory;
}();

