            _this.factory.createModule('modShop').buildComponent(prod.prodImg, prod.prodUrl);
          }

          brandUtil.sSlider('#prodSlider', false, true, false, false, 3, 2000);
          brandUtil.ctaDot();
          document.querySelector('.wayvee-bio__pics').innerHTML = '';
          var bioImgArray = WayveeRiderAssets.riderAssets[projectId].bioPics;

          _this.factory.createModule('modBioImg').buildComponent(bioImgArray);

          document.querySelector('.wayvee-bio__stats').innerHTML = '';
          document.querySelector('.wayvee-bio__about').innerHTML = '';

          _this.factory.createModule('modBioTxt').buildComponent(WayveeRiderAssets.riderAssets[projectId].bioName, WayveeRiderAssets.riderAssets[projectId].bioHomeTown, WayveeRiderAssets.riderAssets[projectId].bioLocale, WayveeRiderAssets.riderAssets[projectId].bioDob, WayveeRiderAssets.riderAssets[projectId].bioVansSince, WayveeRiderAssets.riderAssets[projectId].bioSocialSrc, WayveeRiderAssets.riderAssets[projectId].bioSocialTxt, WayveeRiderAssets.riderAssets[projectId].bioSocialLink, WayveeRiderAssets.riderAssets[projectId].bioAbout, WayveeRiderAssets.riderAssets[projectId].bioCtaTxt, WayveeRiderAssets.riderAssets[projectId].bioCtaLink);

          brandUtil.showLoader(ArcsineViewerApp.loader);
        });
      });
    };

    _this.mqMobile = window.matchMedia('(max-width: 640px)');
    _this.el = $(el);
    _this.loader = _this.el.find('.loading-spinner');
    _this.header = new VBPHeader(_this.el.find('.vbp__container'));
    _this.factory = new ModuleFactory();
    _this.defaultWayvee = 'henry';

    _this.startApp();

    return _this;
  }

  return WayveeHub;
}(AltraEventDispatcher);

var wayveeHub;
$(function () {
  wayveeHub = new WayveeHub('.wayvee');
});});
