customhtml2_copy_cop.customJS:
//Hero play/pause logic
window.addEventListener('DOMContentLoaded', function () {
  var video = document.getElementById('hero-video');
  var image = document.getElementById('hero-video-status-icon');

  // Function to handle the play/pause button click
  function togglePlayPause() {
    if (video.paused) {
      video.play();
      image.src =
        'https://images.jansport.com/is/image/JanSportBrand/altra-difference-pause-button-07062023?fmt=png-alpha';
      image.alt = 'pause button';
    } else {
      video.pause();
      image.src =
        'https://images.jansport.com/is/image/JanSportBrand/altra-difference-play-button-07062023?fmt=png-alpha';
      image.alt = 'play button';
    }
  }

  // Attach click event listener to the play/pause button
  video.addEventListener('click', togglePlayPause);
});

require(['jquery', 'domReady'], function ($, domReady) {
  domReady(function () {
    //Start fade animation
    function checkElementLocation() {
      var $window = $(window);
      var bottom_of_window = $window.scrollTop() + $window.height();

      $('.fade-elem-1').each(function (i) {
        var $that = $(this);
        var bottom_of_object = $that.position().top + $that.outerHeight();

        //if element is in viewport, add the animate class
        if (bottom_of_window > bottom_of_object) {
          $(this).addClass('fade-in');
        }
      });
    }
    // if in viewport, show the animation
    checkElementLocation();

    $(window).on('scroll', function () {
      checkElementLocation();
    });
    //End fade animation
  });
});

