customhtml2.customJS:
window.altraNS = document.location.origin + '/ns/altra/js/';
window.altraScripts = {
  'waypoints': window.altraNS + 'jquery.waypoints.min.js'
};

require(['jquery','domReady'],function($,domReady){
    domReady(function(){
        require([window.altraScripts.waypoints],function(waypoints){
            if(document.location.href.indexOf("debug") > -1){debugger;}

            //
            var clientHeight = $(window).height();
            var clientWidth = $(window).width();
            var resize = null;
            function killROI() {
                // base math taken from 2400w x 1490h - the actual size of the GIANT image we are using
                // if we change the image dimensions, this will need to be recoded..
                // x,y coords
                // slide one-one 1074,408 .why-parallax-text-xray-one
                // slide one-two 1200,575 .why-parallax-text-xray-one-two
                // slide two-one 1830,568
                // slide two-two 1800,1025
                // slide three-one 900,534
                // slide three-two 1054,618
                var scaleX = clientWidth / 2400;
                var scaleY = clientHeight / 1490;
                var scale;
                var offsetX = 0;
                var offsetY = 0;
                if (scaleX > scaleY) {
                // No scale in x axis, stretched in y
                scale = scaleX;
                offsetY = (clientHeight - (1490 * scale)) / 2;
                } else {
                // No scale in y axis, stretched in x
                scale = scaleY;
                offsetX = (clientWidth - (2400 * scale)) / 2;
                }
                var oneOneX = (1074 * scale) + offsetX;
                var oneOneY = (408 * scale) + offsetY ;
                var oneTwoX = (1200 * scale) + offsetX;
                var oneTwoY = (575 * scale) + offsetY;
                var twoOneX = (1830 * scale) + offsetX;
                var twoOneY = (568 * scale) + offsetY ;
                var twoTwoX = (1800 * scale) + offsetX;
                var twoTwoY = (1025 * scale) + offsetY;
                var threeOneX = (900 * scale) + offsetX;
                var threeOneY = (534 * scale) + offsetY ;
                var threeTwoX = (1054 * scale) + offsetX;
                var threeTwoY = (618 * scale) + offsetY;
                $(".why-parallax-text-xray-one").css({top: oneOneY+'px',left: oneOneX+'px'});
                $(".why-parallax-text-xray-one-two").css({top: oneTwoY+'px',left: oneTwoX+'px'});
                $(".why-parallax-text-xray-two").css({top: twoOneY+'px',left: twoOneX+'px'});
                $(".why-parallax-text-xray-two-two").css({top: twoTwoY+'px',left: twoTwoX+'px'});
                $(".why-parallax-text-xray-three").css({top: threeOneY+'px',left: threeOneX+'px'});
                $(".why-parallax-text-xray-three-two").css({top: threeTwoY+'px',left: threeTwoX+'px'});
            }
            function killROIMobile() {
                // base math taken from 750w x 1080h - the actual size of the GIANT image we are using
                // if we change the image dimensions, this will need to be recoded..
                // x,y coords
                // slide one-one 340,518 .why-parallax-text-xray-one
                // slide one-two 434,604 .why-parallax-text-xray-one-two
                // slide two-one 396,604
                // slide two-two 420,998
                // slide three-one 400,590
                // slide three-two 380,978
                var scaleX = clientWidth / 750;
                var scaleY = clientHeight / 1080;
                var scale;
                var offsetX = 0;
                var offsetY = 0;
                if (scaleX > scaleY) {
                // No scale in x axis, stretched in y
                scale = scaleX;
                offsetY = (clientHeight - (1080 * scale)) / 2;
                } else {
                // No scale in y axis, stretched in x
                scale = scaleY;
                offsetX = (clientWidth - (750 * scale)) / 2;
                }
                var oneOneX = (340 * scale) + offsetX;
                var oneOneY = (498 * scale) + offsetY;
                var oneTwoX = (434 * scale) + offsetX;
                var oneTwoY = (575 * scale) + offsetY;
                var twoOneX = (396 * scale) + offsetX;
                var twoOneY = ((604 * scale) + offsetY) + clientHeight;
                var twoTwoX = (420 * scale) + offsetX;
                var twoTwoY = ((998 * scale) + offsetY) + clientHeight;
                var threeOneX = (400 * scale) + offsetX;
                var threeOneY = (580 * scale) + offsetY + (clientHeight * 2);
                var threeTwoX = (380 * scale) + offsetX;
                var threeTwoY = (978 * scale) + offsetY + (clientHeight * 2);
                $(".why-parallax-text-xray-one").css({top: oneOneY+'px',left: oneOneX+'px'});
                $(".why-parallax-text-xray-one-two").css({top: oneTwoY+'px',left: oneTwoX+'px'});
                $(".why-parallax-text-xray-two").css({top: twoOneY+'px',left: twoOneX+'px'});
                $(".why-parallax-text-xray-two-two").css({top: twoTwoY+'px',left: twoTwoX+'px'});
                $(".why-parallax-text-xray-three").css({top: threeOneY+'px',left: threeOneX+'px'});
                $(".why-parallax-text-xray-three-two").css({top: threeTwoY+'px',left: threeTwoX+'px'});
            }
            domReady(function(){
                if(clientWidth < 768) {
                killROIMobile();
                var videoHTML = '<video poster="https://images.altrarunning.com/is/image/altra/video%2Dposter%2Dmobile?$404%2DDESKTOP$" autoplay="" loop="" playsinline="" muted="">'+
                                '<source src="https://images.altrarunning.com/is/content/altra/Marketing Assets/Website/Our Story - Why Altra/Altra_01_WhyAltra_180505_5x4_LQ.mp4" type="video/mp4"'+
                                '</video>';
                $(".why-module-video").append(videoHTML);
                
                // end <768
                } else {
                killROI();
                
                var videoHTML = '<video poster="https://images.altrarunning.com/is/image/altra/video%2Dposter?$hero%2Ddesktop$" autoplay="" loop="" playsinline="" muted="">'+
                                '<source src="https://images.altrarunning.com/is/content/altra/Marketing%20Assets/Website/Our%20Story%20-%20Why%20Altra/Altra_01_WhyAltra_180329A_LQ.mp4" type="video/mp4"'+
                                '</video>';
                $(".why-module-video").append(videoHTML);
                var waypointParallaxOne = new Waypoint({
                    element: $(".why-parallax-static-one"),
                    handler: function(direction){
                    if(direction == "down") {
                        $(".why-parallax-numbers-one, .why-parallax-text-one-copy").fadeIn();
                    } else {
                        $(".why-parallax-numbers-one, .why-parallax-text-one-copy").fadeOut();
                    }
                    }
                });
                var waypointParallaxOneStop = new Waypoint({
                    element: $(".why-parallax-static-one"),
                    handler: function(direction){
                    if(direction == "down") {
                        $(".why-parallax-text-one-copy").fadeOut();
                    } else {
                        $(".why-parallax-text-one-copy").fadeIn();
                    }
                    }, offset: 'bottom-in-view'
                });
                var waypointParallaxOneXray = new Waypoint({
                    element: $(".why-parallax-xray-one"),
                    handler: function(direction) {
                    if(direction == "down"){
                        $(".why-parallax-text-xray-one, .why-parallax-text-xray-one-two").fadeIn();
                    } else {
                        $(".why-parallax-text-xray-one, .why-parallax-text-xray-one-two").fadeOut();
                    }
                    }, offset: '40%'
                });
                var waypointParallaxOneXrayStop = new Waypoint({
                    element: $(".why-parallax-xray-one"),
                    handler: function(direction) {
                    if(direction == "down"){
                        $(".why-parallax-text-xray-one, .why-parallax-text-xray-one-two").fadeOut();
                    } else {
                        $(".why-parallax-text-xray-one, .why-parallax-text-xray-one-two").fadeIn();
                    }
                    },offset: function() {return this.context.innerHeight() - this.adapter.outerHeight() - 200}
                });
                var waypointParallaxTwo = new Waypoint({
                    element: $(".why-parallax-static-two"),
                    handler: function(direction){
                    if(direction == "down") {
                        $(".why-parallax-numbers-two, .why-parallax-text-two-copy").fadeIn();
                        $(".why-parallax-numbers-one, .why-parallax-text-one-copy").fadeOut();
                        // just in case you reload the page without assets...
                        $(".why-parallax-three-one, .why-parallax-text-three-copy").fadeOut();
                    } else {
                        $(".why-parallax-numbers-one, .why-parallax-text-one-copy").fadeIn();
                        $(".why-parallax-numbers-two, .why-parallax-text-two-copy").fadeOut();
                    }
                    }
                });
                var waypointParallaxTwoStop = new Waypoint({
                    element: $(".why-parallax-static-two"),
                    handler: function(direction){
                    if(direction == "down") {
                        $(".why-parallax-text-two-copy").fadeOut();
                    } else {
                        $(".why-parallax-text-two-copy").fadeIn();
                    }
                    }, offset: 'bottom-in-view'
                });
                var waypointParallaxtwoXray = new Waypoint({
                    element: $(".why-parallax-xray-two"),
                    handler: function(direction) {
                    if(direction == "down"){
                        $(".why-parallax-text-xray-two, .why-parallax-text-xray-two-two").fadeIn();
                    } else {
                        $(".why-parallax-text-xray-two, .why-parallax-text-xray-two-two").fadeOut();
                    }
                    }, offset: '40%'
                });
                var waypointParallaxTwoXrayStop = new Waypoint({
                    element: $(".why-parallax-xray-two"),
                    handler: function(direction) {
                    if(direction == "down"){
                        $(".why-parallax-text-xray-two, .why-parallax-text-xray-two-two").fadeOut();
                    } else {
                        $(".why-parallax-text-xray-two, .why-parallax-text-xray-two-two").fadeIn();
                    }
                    },offset: function() {return this.context.innerHeight() - this.adapter.outerHeight() - 200}
                });
                var waypointParallaxThree = new Waypoint({
                    element: $(".why-parallax-static-three"),
                    handler: function(direction){
                    if(direction == "down") {
                        $(".why-parallax-numbers-three, .why-parallax-text-three-copy").fadeIn();
                        $(".why-parallax-numbers-two, .why-parallax-text-two-copy").fadeOut();
                    } else {
                        $(".why-parallax-numbers-two, .why-parallax-text-two-copy").fadeIn();
                        $(".why-parallax-numbers-three, .why-parallax-text-three-copy").fadeOut();
                        // just in case you reload the page without assets...
                        $(".why-parallax-numbers-one, .why-parallax-text-one-copy").fadeOut();
                    }
                    }
                });
                var waypointParallaxThreeXray = new Waypoint({
                    element: $(".why-parallax-xray-three"),
                    handler: function(direction) {
                    if(direction == "down"){
                        $(".why-parallax-text-xray-three, .why-parallax-text-xray-three-two").fadeIn();
                    } else {
                        $(".why-parallax-text-xray-three, .why-parallax-text-xray-three-two").fadeOut();
                    }
                    }, offset: '40%'
                });
                var waypointParallaxThreeXrayStop = new Waypoint({
                    element: $(".why-parallax-xray-three"),
                    handler: function(direction) {
                    if(direction == "down"){
                        $(".why-parallax-text-xray-three, .why-parallax-text-xray-three-two").fadeOut();
                    } else {
                        $(".why-parallax-text-xray-three, .why-parallax-text-xray-three-two").fadeIn();
                    }
                    },offset: function() {return this.context.innerHeight() - this.adapter.outerHeight() - 200}
                });
                var waypointParallaxStop = new Waypoint({
                    element: $(".why-parallax-stop"),
                    handler: function(direction){
                    if(direction == "down") {
                        $(".why-parallax-numbers-three").fadeOut();
                        $(".why-parallax-numbers-three, .why-parallax-text-three-copy").fadeOut();
                    } else {
                        $(".why-parallax-numbers-three").fadeIn();
                        $(".why-parallax-numbers-three, .why-parallax-text-three-copy").fadeIn();
                    }
                    }, offset: 'bottom-in-view'
                });
                var waypointBounceArrow = new Waypoint({
                    element: $(".why-module-video"),
                    handler: function(direction){
                    if(direction == "down"){
                        $(".why-bounce-arrow").fadeOut();
                    } else {
                        $(".why-bounce-arrow").fadeIn();
                    }
                    }
                });
                // end >768
                }
                $(window).on('resize', function () {
                if (resize !== null) {
                    clearTimeout(resize);
                }
                resize = setTimeout(function () {
                    var oldWidth = clientWidth;
                    clientHeight = $(window).height();
                    clientWidth = $(window).width();
                    if(oldWidth < 768) {
                    if(clientWidth > 767) {
                        window.location.reload();
                    } else {
                        killROIMobile();
                    }
                    } else {
                    if(clientWidth < 768){
                        window.location.reload();
                    } else {
                        killROI();
                    }
                    }
                }, 750);
                });
                //end .ready
            });
            //
        }); // libraries
    }); // domReady
}); // require
